useEffect(()=>{
setApp(
  arr.map((x,index) => 
  { return <div key={index}>{x.name}{index}</div>}
  )
)
},[arr])

const cc = () => {
setArr(x => [...x,{name:'item'}])
}

  return (
  <>
  <article className='rounded mt-5'>
  <h1 className='text-center p-3 '>Grocery Bud</h1>
  <div className ='d-flex justify-content-center align-items-center'>
    <input placeholder='e.g. egges' type='text' style={{
      height:'38px',
      width:'70%',
      border:'none',
      outline:'none',
      padding:'20px'
    }} /><button type='submit' className='btn p-2 bg-info' 
    onClick={cc}>Submit</button>
  </div>
{
app 
}


  <h5 className='text-center' style={{
    color:'red',
    letterSpacing:'2px' ,
    cursor:'pointer',
    opacity:'0.8' ,
  }}>Clear items</h5>
  </article>
  </>
    );
}

export default App;
