import './cs.css'
import {useState,useEffect, useReducer} from 'react'
import SingleMap from './SingleMap'
import Alert from './Alert'

function App() {
  let [val,setVal] = useState('')
  let [count,setCount] = useState(1)
  const [app,setApp] = useState(null)
  let [arr,setArr] = useState([{name:'Youssef Zydan' , f : 0 }])
  let [state,dispatch] = useReducer(reducer,null)
  const [alert,setAlert] = useState(false)
  const [currentNameEdit,setcurrentNameEdit] = useState()
  const [conditions,setConditions] = useState({
  msg:'',type:'',mark:false
  })

function reducer(state,action) {
  switch(action.type){
    case 'delete': return setArr(arr.filter(x=>x.f != action.id)) ;
    case 'edit' : return setVal(action.name)
  }
}


const y = (id) => {
  dispatch({
    type: 'delete',
    id : id
  })
}

const y2 = (name,f) => {
  dispatch({
    type: 'edit',
    name : name 
  })
  setAlert(true)
  setcurrentNameEdit(f)
}


useEffect(()=>{
setApp(
  arr.map((x,index)=> <SingleMap {...x} key={index}  yy={y} yy2={y2} f={x.f} name={x.name} />)
)
},[arr])


const cc = () => {
if(alert){
for(let i = 0;i < arr.length;i++){
if(arr[i].f == currentNameEdit){
arr[i].name = val

setArr(x => [...x])

}
}
setAlert(false)
}else{
setArr(x => [...x,{name:val , f : count }])
setCount(count + 1)
}
setVal('')
}
  return (
  <>
  <article className='rounded mt-5'>
    {conditions && <Alert  />}
  <h1 className='text-center p-3 '>Grocery Bud</h1>
  <div className ='d-flex justify-content-center align-items-center'>
    <input 
    onChange={(e)=>setVal(e.target.value)} value={val}
    placeholder='e.g. egges' type='text' style={{
      height:'38px',
      width:'70%',
      border:'none',
      outline:'none',
      padding:'20px'
    }} /><button type='submit' className='btn p-2 bg-info' 
    onClick={cc}>{alert?'Edit':'Submit'}</button>
  </div>
{
app 
}


  <h5 className='text-center' style={{
    color:'red',
    letterSpacing:'2px' ,
    cursor:'pointer',
    opacity:'0.8' ,
    marginTop:'10px'
  }}
  onClick={()=>setArr([])}
  >Clear items</h5>
  </article>
  </>
    )};


export default App;
