import './cs.css'
import {useState,useEffect} from 'react'
import SingleMap from './SingleMap'


function App() {
  let [val,setVal] = useState('')
  const [app,setApp] = useState(null)
  let [arr,setArr] = useState([])
  let [count,setCount] = useState(0)


useEffect(()=>{
setApp(
  arr.map((x,index)=> <SingleMap {...x} key={index} id={index} array={arr}  val={val} />)
)
},[arr])


const cc = () => {
setArr(x => [...x,{name:val , f : count }])
setCount(count + 1)
}

  return (
  <>
  <article className='rounded mt-5'>
  <h1 className='text-center p-3 '>Grocery Bud</h1>
  <div className ='d-flex justify-content-center align-items-center'>
    <input 
    onChange={(e)=>setVal(e.target.value)}
    placeholder='e.g. egges' type='text' style={{
      height:'38px',
      width:'70%',
      border:'none',
      outline:'none',
      padding:'20px'
    }} /><button type='submit' className='btn p-2 bg-info' 
    onClick={cc}>Submit</button>
  </div>
{
app 
}


  <h5 className='text-center' style={{
    color:'red',
    letterSpacing:'2px' ,
    cursor:'pointer',
    opacity:'0.8' ,
    marginTop:'10px'
  }}
  onClick={()=>setArr([])}
  >Clear items</h5>
  </article>
  </>
    )};


export default App;
